const hex2RGB = (() => {
    const CAPTURE_REGEX = /^#?([a-f\d]{2})([a-f\d]{2})([a-f\d]{2})$/i;
    return hex_value => {
        const result = hex_value.match(CAPTURE_REGEX);
        return result ? {
            r : parseInt(result[1], 16),
            g : parseInt(result[2], 16),
            b : parseInt(result[3], 16)
        } : null;
    };
})();

const calculate_illuminance = (() => {
    const formula = value => {
        value /= 255;
        return (value <= 0.03928)
            ? value / 12.92
            : Math.pow(((value + 0.055) / 1.055), 2.4);
    };
    return hex_color => {
        let { r, g, b } = hex2RGB(hex_color);
        [r, g, b] = [r, g, b].map(formula);
        return r * 0.2126 + g * 0.7152 + b * 0.0722;
    };
})();","readOnly":false,"theme":"monokai","lineNumbers":true,"styleActiveLine":true,"cursorBlinkRate":0,"extraKeys":{"Ctrl-Space":"autocomplete"},"autofocus":false,"autorefresh":true,"scrollbarStyle":"overlay","mode":{"name":"javascript","globalVars":true},"rows":24,"font_size":"16px","line_height_ratio":1.35,"line_height_with_ratio":32.400000000000006,"cursor":{"line":9,"ch":39,"sticky":"before","xRel":66.484375},"scroll_position":{"x":0,"y":71}},"is_loaded":true,"is_activated":false},{"type":"xterm","title":"XTerm","options":{"rows":24,"cols":100,"termName":"xterm-256color","fontSize":16,"fontFamily":"MonacoB, Inconsolata","fontWeight":"bold","cursorStyle":"block","drawBoldTextInBrightColors":true,"tabStopWidth":4,"screenKeys":true,"macOptionIsMeta":true,"macOptionClickForcesSelection":true},"state":{"cursor":{"pos":{"x":12,"y":11},"attr":{"fg":0,"bg":0},"state":1},"buffers":{"alt":{"buffer":"[37;48;5;234m 11 [38;5;255;49m    get [48;5;234mr[49mecorder_stream [38;5;222m()[38;5;255m [38;5;207m{[39m                                                   [38;5;123;48;5;234m [39;49m                
